mapper.select:
SELECT si.id, si.name, si.type_id AS typeId, si.type, si.amount, si.unit, si.content, si.price, si.create_date AS createDate, si.is_in, si.to_user_id, cy.`name` AS consumableName FROM stock_info si LEFT JOIN consumable_type cy ON (cy.id = si.type_id) WHERE si.is_in != 0
AND si.name LIKE CONCAT('%',#{stockInfo.name},'%')
AND si.type LIKE CONCAT('%',#{stockInfo.type},'%')
AND si.type_id = #{stockInfo.typeId}
AND si.is_in = #{stockInfo.isIn}

SELECT si.id, si.name, si.type_id AS typeId, si.type, si.amount, si.unit, si.content, si.price, si.create_date AS createDate, ct.`name` AS consumableName FROM stock_info si LEFT JOIN student_info stu ON ( stu.id = si.to_user_id ) LEFT JOIN consumable_type ct ON ( ct.id = si.type_id ) WHERE 1 = 1 AND si.is_in = 2 AND stu.user_id = #{ stockInfo.userId }
AND si.name LIKE CONCAT('%',#{stockInfo.name},'%')
AND si.type LIKE CONCAT('%',#{stockInfo.type},'%')
AND si.type_id = #{stockInfo.typeId}
